return {
	["id"] = 387,
	["title_alternative"] = { "Charcoal Feather Federation", "Haibane Renmei" },
	["characters"] = {{["mal_id"] = 1995,   ["firstname"] = "Rakka", ["lastname"] = "Rakka",  ["nicknames"] = {  "Rakka", }, ["description"] = "Rakka is the newest addition to Old Home and the main character of Haibane Renmei. The series largely focuses on Rakka\'s adjustment to the strange new world of Glie. Although happy to have several new friends, Rakka cannot help but wonder who she was before she became a Haibane and if her family is somewhere in this world. She feels a connection with the birds of Glie, especially since her dream involved falling with only a crow as company. She is very amiable and quickly becomes attached to her new family.",}, {["mal_id"] = 1996,   ["firstname"] = "Reki", ["lastname"] = "Reki",  ["nicknames"] = {  "Reki", }, ["description"] = "Reki is the unofficial leader of Old Home. She\'s tough when she needs to be but is also caring and compassionate. Her role at Old Home is to care for the young Haibane as well as take care of New Feathers like Rakka. Reki is a major part of Rakka\'s life in Glie as she was the one who discovered her cocoon and stayed by her side during the painful process of growing wings. Reki is most always cheerful, at least on the surface. We receive hints of a darker past throughout the series. Despite whatever these problems from her past may be, Reki is a strong and reliable person, which explains why the other Haibane respect her as they do.",}, {["mal_id"] = 7392,   ["firstname"] = "Hana", ["lastname"] = "Hana",  ["nicknames"] = {  "Hana", }, ["description"] = "No biography written.",}, {["mal_id"] = 2005,   ["firstname"] = "Hikari", ["lastname"] = "Hikari",  ["nicknames"] = {  "Hikari", }, ["description"] = "Hikari has been around Old Home the longest after Reki and Nemu at approximately four years. She is a kind person though she sometimes acts before thinking. When she saw the mold for the halo that she was to make for Rakka she was struck with the idea of pouring dough in it to make halo-like pastries. It is because of this that the little ones think that Rakka smells like cake. Not surprisingly she works at a bakery in town. Though modest about her contributions, her co-workers insist that she does more than her share, which is why they don\'t mind when her friends stop by to have a few treats while visiting her. Hikari may be a bit mischievous at times but she always means well and is the first to volunteer to help.",}, {["mal_id"] = 2000,   ["firstname"] = "Hyouko", ["lastname"] = "Hyouko",  ["nicknames"] = {  "Hyouko", }, ["description"] = "The leader of another group of Haibane in town (the mixed-gender Abandoned Factory nest). Has a history with Reki.",}, {["mal_id"] = 1999,   ["firstname"] = "Kana", ["lastname"] = "Kana",  ["nicknames"] = {  "Kana", }, ["description"] = "Kana is the most mechanically inclined of the Haibane and spends much of her free time tinkering with machines, especially the clock tower at Old Home. She is fascinated by both the clock\'s intricate workings as well as the work notes mysteriously left behind by the clock\'s previous inhabitant. She is a bit of a tomboy, preferring a wrench to a needle any day. She is sometimes seems mean but all of her jokes are in good fun and she cares very much for her fellow Haibane.",}, {["mal_id"] = 2002,   ["firstname"] = "Kuramori", ["lastname"] = "Kuramori",  ["nicknames"] = {  "Kuramori", }, ["description"] = "A Haibane who once took care of Nemu and Reki. Despite her delicate health, she was kind and helpful. She no longer lives in Old Home.",}, {["mal_id"] = 1997,   ["firstname"] = "Kuu", ["lastname"] = "Kuu",  ["nicknames"] = {  "Kuu", }, ["description"] = "Kuu is the youngest Haibane in the older group, having only been at Old Home two years more than Rakka. She is seemingly full of boundless energy and is nearly always cheerful. Rakka and Kuu became close friends shortly after Rakka arrived at Old Home. Kuu confides in Rakka that she had wished her to be a younger sister but was happy that Rakka wound up being older than her since she would have been placed with the younger group otherwise. Kuu is curious and enjoys trying out different things, which can sometimes result in disaster. She tried Reki\'s scooter and crashed into a telephone pole, and when she tried Hikari\'s glasses she became dizzy and fell down the stairs. She occasionally works at a cafe in town and is seemingly paid in sugar cubes.",}, {["mal_id"] = 2004,   ["firstname"] = "Midori", ["lastname"] = "Midori",  ["nicknames"] = {  "Midori", }, ["description"] = "Kuu is the youngest Haibane in the older group, having only been at Old Home two years more than Rakka. She is seemingly full of boundless energy and is nearly always cheerful. Rakka and Kuu became close friends shortly after Rakka arrived at Old Home. Kuu confides in Rakka that she had wished her to be a younger sister but was happy that Rakka wound up being older than her since she would have been placed with the younger group otherwise. Kuu is curious and enjoys trying out different things, which can sometimes result in disaster. She tried Reki\'s scooter and crashed into a telephone pole, and when she tried Hikari\'s glasses she became dizzy and fell down the stairs. She occasionally works at a cafe in town and is seemingly paid in sugar cubes.",}, {["mal_id"] = 1998,   ["firstname"] = "Nemu", ["lastname"] = "Nemu",  ["nicknames"] = {  "Nemu", }, ["description"] = "Nemu is the oldest member of Old Home, having been there for approximately nine years. Nemu lives up to her namesake by being rather sedate and often sleeping in or taking naps. She\'s even been known to catch the occasional snooze at work, the town\'s library, but her boss doesn\'t seem to mind since she does such a good job. After a turbulent start she formed a close friendship with Reki when she arrived seven years ago.",}, {["mal_id"] = 28074,   ["firstname"] = "Shorta", ["lastname"] = "Shorta",  ["nicknames"] = {  "Shorta", }, ["description"] = "One of the \"Young Feathers\" of Old Home. He insists his cocoon dream was about shortcake, hence his name.",}, {["mal_id"] = 2003,   ["firstname"] = "Sumika", ["lastname"] = "Sumika",  ["nicknames"] = {  "Sumika", }, ["description"] = "One of the \"Young Feathers\" of Old Home. He insists his cocoon dream was about shortcake, hence his name.",}, {["mal_id"] = 2001,   ["firstname"] = "Communicator", ["lastname"] = "The",  ["nicknames"] = {  "The Communicator", }, ["description"] = "A presiding official of the Haibane Renmei who gives spiritual advice and worldly assistance and on occasion doles out punishment to wayward Haibane.",}},
