    [271] = {
      firstname = "Itachi",
      description = "Age: 17-18 (I); 21 (II) Birthday: June 9th (Gemini)\
 Height: 175 cm (I); 178 cm (II)\
 Weight: 57 kg (I); 58 kg (II)\
 Blood Type: AB\
 Nature Type: Fire and Water\
